    (313, 'に至って', 'cho đến khi…, mãi đến…', '失敗が明らかになってに至って謝った。', 'Mãi đến khi thất bại rõ ràng mới xin lỗi.', 'N1', True),
    (314, 'にかたくない', 'không khó để…', '想像にかたくない。', 'Không khó để tưởng tượng.', 'N1', True),
    (315, 'にして', 'mãi đến…, tận…', '彼は50歳にして大学に入った。', 'Mãi đến 50 tuổi anh ấy mới vào đại học.', 'N1', True),
    (316, 'にして初めて', 'chỉ khi… mới…', '教師にして初めて分かることだ。', 'Chỉ khi trở thành giáo viên mới hiểu.', 'N1', True),
    (317, 'にしても', 'dù là…, ngay cả…', '冗談にしてもひどい。', 'Dù là nói đùa thì cũng quá đáng.', 'N1', True),
    (318, 'にしろ / にせよ', 'cho dù…, dù là…', '行くにしろ行かないにしろ連絡してください。', 'Dù đi hay không thì hãy liên lạc.', 'N1', True),
    (319, 'にもかかわらず', 'mặc dù…, dù vậy…', '雨にもかかわらず試合は行われた。', 'Mặc dù mưa nhưng trận đấu vẫn diễn ra.', 'N1', True),
    (320, 'にもまして', 'hơn nữa…, hơn cả…', '今年は去年にもまして寒い。', 'Năm nay lạnh hơn cả năm ngoái.', 'N1', True),
    (321, 'にあたる', 'tương ứng với…, tương đương…', '日本の正月はベトナムのテトにあたる。', 'Tết Nhật tương đương với Tết Việt Nam.', 'N1', True),
    (322, 'に足る（にたる）', 'xứng đáng…, đủ để…', '信頼に足る人物だ。', 'Là người đáng tin cậy.', 'N1', True),
    (323, 'とあって', 'vì là…, do… (đặc biệt)', '休日とあって道が混んでいる。', 'Vì là ngày nghỉ nên đường đông.', 'N1', True),
    (324, 'といい / とよい', 'ước gì…, mong là…', '晴れるといい。', 'Ước gì trời nắng.', 'N1', True),
    (325, 'といえども', 'cho dù…, ngay cả…, dù là…', '子供といえども許されない。', 'Dù là trẻ con cũng không được phép.', 'N1', True),
    (326, 'といったところだ', 'chỉ khoảng…, cỡ khoảng…', '給料は20万円といったところだ。', 'Lương chỉ khoảng 200.000 yên.', 'N1', True);
